package no.ssb.kostra.test.control.sensitiv.soshjelp;

import no.ssb.kostra.control.sensitiv.soshjelp.ControlKommunenummer;
import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

/**
 * Created by ojj on 16.01.2019.
 */
public class ControlKommunenummerTest {

    String ok_1 = "";
    String ok_2 = "";
    String ok_3 = "";
    String fail = "";
    ControlKommunenummer t;

    //
    @Before
    public void beforeTest() {
        t = new ControlKommunenummer();
//		// -----
//		// -----
//		// -----
        ok_1 = "";
        ok_2 = "";
        ok_3 = "";
        fail = "";

    }

    @Test
    public void testControlKommunenummerOK1() {
        assertFalse(t.doControl(ok_1, 1, "100300", ""));
    }

    @Test
    public void testControlKommunenummerOK2() {
        assertFalse(t.doControl(ok_2, 1, "030104", ""));
    }

    @Test
    public void testControlKommunenummerOK3() {
        assertFalse(t.doControl(ok_3, 1, "030100", ""));
    }


    @Test
    public void testControlKommunenummerFail() {
        assertTrue(t.doControl(fail, 1, "010100", ""));
    }


}
